    public static int name(List&lt;Integer&gt; numbers) {
        
        int maxSum = numbers.get(0);
        int tempMax = maxSum;
        
        for (int i = 1; i &lt; numbers.size(); i++) {
            tempMax = Math.max(numbers.get(i), numbers.get(i) + tempMax);
            maxSum = Math.max(maxSum, tempMax);
        }
        
