      &quot;2543-2,2543-2&quot;,
      &quot;2543-4&quot;,
      &quot;25543-4,25743-4&quot;
    ]
    const replacement=&#39;1234&#39;

    const result=input.map(e =&gt; e.split(&#39;,&#39;).map(e =&gt; e.replace(/\d+/,replacement)).join(&#39;,&#39;))

