    var wrap = document.getElementById(&#39;wrap&#39;)

    btn.addEventListener(&#39;click&#39;, function(event) {
      event.stopPropagation()
      if (event.cancelable) {
        event.preventDefault();
        return;
      }
      alert(&#39;Alert on btn is triggered&#39;)
    })


    function alerted() {
      alert(&#39;Alerted fired&#39;)
    }

    wrap.addEventListener(&#39;click&#39;, function() {
