    class UserPost extends React.Component {
      constructor(props) {
        super(props);
        this.state = {
          posts: {}
        }
      }
    
      componentDidMount() {
        firebase
        .database()
        .ref()
        .child(&quot;post&quot;)
        .on(&quot;value&quot;, snapshot =&gt; {
          this.setState({ posts : snapshot.val()})
        })
      }

      render() {
        return (
          &lt;div className=&quot;userpost&quot;&gt;
            {Object.keys(this.state.posts).map((item, i) =&gt; (
                  &lt;Post
                    key={i}
                    title={this.state.posts[item].title}
