    def func(*args):
        out = {}
        factor = 1
        for arg in args:
            if isinstance(arg, dict):
                if not out:
                    out = arg.copy()
                elif out.keys() != arg.keys():
                    raise KeyError(&#39;not same keys&#39;)
                else:
                    for key in out:
                        out[key] *= arg[key]
            else:
                factor *= arg
