.mod

    tuple edge
    {
       key int o;
       key int d;
       int weight;
    }
    
    {edge} edges=...;
    
    {int} nodes={i.o | i in edges} union {i.d | i in edges};
    int st=1; // start
    int en=8; // end
    
    dvar int obj; // distance
    dvar boolean x[edges]; // do we use that edge ?
    
    minimize obj;
    
    subject to
    {
    obj==sum(e in edges) x[e]*e.weight;
    
    forall(i in nodes) 
