    # FAIL! 403 (need user agent)
    headers = { &quot;Content-Type&quot; =&gt; &quot;application/sparql-query&quot; }
    resp= Excon.post(&#39;https://query.wikidata.org/sparql&#39;, body: sparql, headers: headers)
    puts &quot;POST sparql-query&quot;, resp.status, resp.body[0,100], &quot;\n&quot;
    
    # SUCCESS!
