    $(document).ready(function() {
      var audio;
      //Hide pause
      $(&#39;#pause&#39;).hide();

      initAudio($(&#39;#playlist &gt; div:first-child li&#39;));

      function initAudio(element) {
        var song = element.attr(&#39;song&#39;);
        var title = element.text();
        var artist = element.attr(&#39;artist&#39;);

        // Audio Object

        audio = new Audio(&#39;media/&#39; + song);
        //insert audio info
        $(&#39;.artist&#39;).text(artist);
        $(&#39;.title&#39;).text(title);
        console.log(audio);
        $(&#39;#playlist li&#39;).removeClass(&#39;active&#39;);
        element.addClass(&#39;active&#39;);
      }

      // play button
      $(&#39;#play&#39;).click(function() {
        audio.play();
        $(&#39;#play&#39;).hide();
        $(&#39;#pause&#39;).show();
        showDuration();
      });

      //Pause button

      $(&#39;#pause&#39;).click(function() {
        audio.pause();
        $(&#39;#play&#39;).show();
        $(&#39;#pause&#39;).hide();
      });

      //Next button
      $(&#39;#next&#39;).click(function() {
        audio.pause();
        var next = $(&#39;#playlist li.active&#39;).next();
        if (next.length == 0) {
          next = $(&#39;#playlist li:first-child&#39;);
        }
        initAudio(next);
        audio.play();
        showDuration();
      });


      $(&#39;#playlist li&#39;).click(function() {
        audio.pause();
        initAudio($(this));
        $(&#39;#play&#39;).hide();
        $(&#39;#pause&#39;).show();
        audio.play();
        showDuration();
