      //Time/showDuration

      function showDuration() {
        $(audio).bind(&#39;timeupdate&#39;, function() {
          //Get Hours and minutes
          var s = parseInt(audio.currentTime % 60);
          var m = parseInt(audio.currentTime / 60) % 60;
          if (s &lt; 10) {
            s = &#39;0&#39; + s;
