&lt;!-- language: lang-js --&gt;

    const theFunction = console.log;

    document.querySelector(&#39;ul&#39;).addEventListener(&#39;click&#39;, (e) =&gt; {
      if (!e.target.matches(&#39;li&#39;)) {
        return;
      }
      const param = &#39;id&#39; + e.target.dataset.param;
      theFunction(param);
    });

&lt;!-- language: lang-html --&gt;

