        }    
      });
    });

    function haveIntersection(r1, r2) {
      return !(
        r2.x &gt; r1.x + r1.width/2 ||
        r2.x + r2.width/2 &lt; r1.x ||
        r2.y &gt; r1.y + r1.height/2 ||
        r2.y + r2.height/2 &lt; r1.y
      );
    }

