		boolean[][] cells;
		boolean[][] newCells;
	
		public SimulationThread(MyPanel myPanel) {
			this.myPanel = myPanel;
			cells = new boolean[GAME_SIZE][GAME_SIZE];
			for (int i = 1; i &lt; cells.length - 1; i++) {
				for (int j = 1; j &lt; cells[0].length - 1; j++) {
					cells[i][j] = Math.random() &gt; 0.5;
