					for (int i = 1; i &lt; cells.length - 1; i++) {
						for (int j = 1; j &lt; cells[0].length - 1; j++) {
							cells[i][j] = newCells[i][j];
						}
					}
				}
				myPanel.repaint();
			}
	
		}
	
