      height: 60vh;
      display: flex;
    }

    .turbotobias-contact-section-wrapper p {
      font-size: 24px;
    }

    #wrap {
      width: 550px;
      margin: 20px auto 0;
      height: 1000px;
    }

    h1 {
      margin-bottom: 20px;
      text-align: center;
      font-size: 35px;
      font-family: open sans, Arial, sans-serif;
      color: white;
    }

    #form-wrap {
      overflow: hideen;
      height: 447px;
      position: relative;
      top: 0px;
      transition: all 1s ease-in-out 0.3s;
    }

    #form-wrap:before {
      content: &quot;&quot;;
      position: absolute;
      bottom: 128px;
      left: 0px;
      background: url(https://www.onlinefox.dk/wp-content/uploads/2019/09/brev-before.png) !important;
      width: 530px;
      height: 317px;
    }

    #form-wrap:after {
      content: &quot;&quot;;
      position: absolute;
      bottom: 0px;
      left: 0;
      background: url(https://www.onlinefox.dk/wp-content/uploads/2019/09/brev-after.png) !important;
      width: 530px;
      height: 259px;
      z-index: 999;
    }

    #form-wrap.hide:after,
    #form-wrap.hide:before {
      display: none;
    }

    #form-wrap:hover {
      height: 777px;
      top: -200px;
    }

    form {
      background: white;
      position: relative;
      top: 200px;
      overflow: hidden;
      height: 100px;
      width: 400px;
      margin: 0px auto !important;
      padding: 20px !important;
      border: 1px solid white;
      border-right: 3px;
      transition: all 1s ease-in-out 0.3s;
    }

    #form-wrap:hover form {
      height: 530px;
    }

    label {
      margin: 11px 20px 0 0;
      font-size: 15px;
      color: gray;
      text-transform: uppercase;
    }

    input[type=&quot;text&quot;],
    textarea {
      font: 14px normal uppercase, arial, serif;
      color: dimgray;
      background: none;
      width: 380px;
      height: 37px;
      padding: 0px 10px;
      margin: 0 0 10px 0;
      border: 1px solid #f8f5f1;
      border-radius: 5px;
    }

    textarea {
      height: 80px;
      padding-top: 14px;
    }

    textarea:focus,
    input[type=&quot;text&quot;]:focus {
      background: white;
    }

    #form-wrap input[type=&quot;submit&quot;] {
      position: relative;
      font-family: open sans, Arial, sans-serif;
      font-size: 24px;
      color: gray;
      width: 100%;
      text-align: center;
      opacity: 0;
      background: none;
      cursor: pointer;
      border-radius: 3px;
      border: none !important;
      transition: opacity 0.7s ease-in-out 0s;
    }

    #form-wrap:hover input[type=&quot;submit&quot;] {
      z-index: 1;
      opacity: 1;
      transition: opacity 0.5s ease-in-out 1.3s;
    }

    .turbotobias-contact-row {
      display: flex;
      width: 80%;
      max-width: 1080px;
      margin: auto;
      padding-bottom: 50px;
      flex-direction: row;
      flex-wrap: wrap;
    }

    @media (max-width: 980px) {
      .turbotobias-contact-row {
        padding: 30px 0;
        width: 100%;
      }
      #form-wrap:before,
      #form-wrap:after {
        width: 100%;
        background-size: contain!important;
        background-repeat: no-repeat!important;
      }
      #form-wrap:after {
        height: 220px;
        bottom: 30px;
      }
      #wrap {
        width: 445px;
      }
      form {
        width: 320px;
      }
    }

    @media (max-width: 480px) {
      #wrap {
        width: 320px;
      }
      #form-wrap:after {
        height: 240px;
      }
      form {
        height: 0;
        width: 90%;
      }
      input[type=&quot;text&quot;],
      textarea {
        width: 100%;
      }
      .turbotobias-contact-section-wrapper p {
        font-size: 19px;
        text-align: center;
        margin-top: -20px;
        height: 60px;
      }
    }

