I wrote a n-section algorithm for finding roots of a function. The working principle is exactly the same as is bisection method, only the range is divided into _N_ equal parts instead. This is my C code:

    /*
    	Compile with: clang -O3 -o nsect nsect.c -Wall -DCOUNT=5000000 -DNSECT=? -funroll-loops
    */
    
    #include &lt;math.h&gt;
    #include &lt;stdio.h&gt;
    #include &lt;stdlib.h&gt;
    #include &lt;string.h&gt;
    #include &lt;sys/time.h&gt;
    
    #define N 6
    
    #ifndef COUNT
    #error COUNT not defined!
    #endif
    
    #ifndef NSECT
    #define NSECT 2
    #endif
    
    float polynomial[N];
    float horner( const float poly[N], float x )
    {
    	float val = poly[N-1];
    	for ( int i = N - 2; i &gt;= 0; i-- )
    		val = poly[i] + x * val;
    	return val;
    }
    
    float f( float x )
    {
    	return horner( polynomial, x );
    }
    
    float nsect( float a, float b, const float eps_x )
    {
    	float fa = f( a );
    	float fb = f( b );
    	if ( fa == 0 ) return a;
    	else if ( fb == 0 ) return b;
    	else if ( fa * fb &gt; 0 ) return 0;
    
    	float x[NSECT];
    	float fx[NSECT];
    
    	while ( b - a &gt; eps_x )
    	{	
    		x[0] = a;
    		if ( ( fx[0] = f( x[0] ) ) == 0 ) return x[0];
    		
    		int found = 0;
    		for ( int i = 0; i &lt; NSECT - 1; i++ )
    		{
    			x[i + 1] = a + ( b - a ) * (float)( i + 1 ) / NSECT;
    			if ( ( fx[i + 1] = f( x[i + 1] ) ) == 0 )
    				return x[i + 1];
    			else if ( fx[i] * fx[i + 1] &lt; 0 )
    			{
    				a = x[i];
    				b = x[i + 1];
    				found = 1;
    				break;
    			}
    		}
    		
    		if ( !found )
    			a = x[NSECT - 1]; 
    
    	}
    
    	return ( a + b ) * 0.5f;
    }
    
