func ==(lhs: String, rhs: @autoclosure ()-&gt;Int) -&gt; Bool {
    guard let stringIntValue = Int(lhs) else { return false }
    return stringIntValue == rhs()
}

func ==(lhs: Int, rhs: String) -&gt; Bool {
