                    urlParameters: oParameter,
                    success: function (oData) {
                        resolve(oData);
                    }.bind(this),
                    error: function (oError) {
                        reject(oError);
                    }.bind(this)
                });
            })
            );
        }.bind(this));
        
    oTable.setBusy(true);
    
    Promise.all(aPromises).then(
        // aData - array of each oData by the success callbacks
        function(aData) {
            oTable.setBusy(false);
            oTable.removeSelections();
            this.getModel().refresh();
        }.bind(this),
        // oError - the first error that happens
        function(oError) {
            oTable.setBusy(false);
            this.clearMessages();
            var sMessageBoxText = this.getResourceBundle().getText(&quot;errorMessageBox&quot;);
            this.showMessage(oError, sMessageBoxText, false);
        }.bind(this)
    );

