In User model

    &lt;?php
    
    namespace App;
    
    use Illuminate\Database\Eloquent\Model;
    use App\Post;
    
    class User extends Model
    {
        /**
         * Get the posts for the user.
         */
        public function comments()
        {
            return $this-&gt;hasMany(&#39;App\Post&#39;);
        }
    }

