&lt;p&gt;You entered here: @Value&lt;/p&gt;

@code {

    private string _value;

    [Parameter]
    public string Value {
        get =&gt; _value;
        set {
            if(Equals(value, _value)) {
                return;
            }
            _value = value;
            OnValueChanged.InvokeAsync(value).GetAwaiter().GetResult();
        }
