        def get_context_data(self, **kwargs):
            context = super().get_context_data(**kwargs)
            # Update context with data needed
            context[&#39;foo&#39;] = &#39;bar&#39;
            context[&#39;foo_2&#39;] = &#39;bar_2&#39;
            return context

