&lt;!-- language: lang-html --&gt;

    &lt;script src=&quot;https://cdnjs.cloudflare.com/ajax/libs/jquery/3.3.1/jquery.min.js&quot;&gt;&lt;/script&gt;
    &lt;div&gt;
      &lt;label&gt;Points 1&lt;/label&gt; &lt;input id=&quot;points1&quot; type=&quot;text&quot;&gt;
    &lt;/div&gt;
    &lt;div&gt;
      &lt;label&gt;Points 2&lt;/label&gt; &lt;input id=&quot;points2&quot; type=&quot;text&quot;&gt;
