      &lt;div className=&quot;App&quot;&gt;
        &lt;Nav /&gt;
        &lt;Switch&gt;
          &lt;Route path=&quot;/&quot; exact component={Main} /&gt;
          &lt;Route path=&quot;/about&quot; exact component={About} /&gt;
          &lt;Route path=&quot;/contact&quot; exact component={Contact} /&gt;
          &lt;Route path=&quot;/shop&quot; exact component={Shop} /&gt;
          &lt;Route component={Error404} /&gt;
        &lt;/Switch&gt;
