pub fn get_instruction(file: &amp;[u8], counter: usize) {
    let ins = u32::from(file[counter]) &lt;&lt; 24
        | u32::from(file[counter + 1]) &lt;&lt; 16
        | u32::from(file[counter + 2]) &lt;&lt; 8
