        assignArrayByPath($output, $key, $entry);
    }
    
    function assignArrayByPath(&amp;$arr, $path, $value, $separator=&#39;.&#39;) {
        $keys = explode($separator, $path);
        
        foreach ($keys as $key) {
            $arr = &amp;$arr[$key];
        }
        
        $arr = $value;
    }
    
    echo json_encode($output, JSON_PRETTY_PRINT);
