    const toJson = (data) =&gt; {
      return Object.keys(data).reduce((a, k) =&gt; {
        k.split(&#39;.&#39;).reduce((r, e, i, a) =&gt; {
          return r[e] || (r[e] = (a[i + 1] ? {} : data[k]))
        }, a)
        return a
      }, {})
    }

