    public function guess($path)
    {
        if (!is_file($path)) {
            throw new FileNotFoundException($path);
        }

        if (!is_readable($path)) {
            throw new AccessDeniedException($path);
        }

        ob_start();

        passthru(sprintf($this-&gt;cmd, escapeshellarg($path)), $return);
        if ($return &gt; 0) {
