```typescript
interface FooAction {
    type: &quot;foo&quot;;
    data: any;
}

interface BarAction {
    type: &quot;bar&quot;;
    data: any;
}

type AllActions = (FooAction | BarAction)[&quot;type&quot;]; // &quot;foo&quot; | &quot;bar&quot;
