    &lt;/div&gt;

&lt;!-- end snippet --&gt;

&lt;!-- begin snippet: js hide: false console: true babel: false --&gt;

&lt;!-- language: lang-js --&gt;

    window.onload = function() {
        var number = document.getElementById(&#39;row&#39;).offsetHeight;
        var numberVh = number * 0.10718113612004287;
