    do {
        var isSSNField = ObjCBool(true)
        try videoURL = NSURL.init(resolvingBookmarkData: decoded, options: .withoutUI, relativeTo: nil, bookmarkDataIsStale: &amp;isSSNField)
        print(videoURL)
    } catch {
        print(error)
    }
               
}
