		var last = slices.Last();
		var isSpecific = last.Length == 6 &amp;&amp; last.All(char.IsDigit);
		var result = string.Join(&quot;_&quot;, isSpecific ? slices.Take(slices.Length - 1) : slices) + Path.GetExtension(source);
		Console.WriteLine(result);
	}

