    vector&lt;string&gt; colNames = {&quot;name&quot;, &quot;date&quot;, &quot;price&quot;};
    vector&lt;DATA_TYPE&gt; colTypes = {DT_STRING, DT_DATE, DT_DOUBLE};
    int colNum = 3, rowNum = 10000, indexCapacity=10000;
    ConstantSP table = Util::createTable(colNames, colTypes, rowNum, indexCapacity);
    vector&lt;VectorSP&gt; columnVecs;
    for(int i = 0; i &lt; colNum; ++i)
        columnVecs.push_back(table-&gt;getColumn(i));

    int array_dt_buf[Util::BUF_SIZE]; //ÂÆö‰πâdateÂàóÁºìÂÜ≤Âå∫Êï∞ÁªÑ
    double array_db_buf[Util::BUF_SIZE]; //ÂÆö‰πâpriceÂàóÁºìÂÜ≤Âå∫Êï∞ÁªÑ

    int start = 0;
    int no=0;
    while (start &lt; rowNum) {
        size_t len = std::min(Util::BUF_SIZE, rowNum - start);
        int *dtp = columnVecs[1]-&gt;getIntBuffer(start, len, array_dt_buf); //dtpÊåáÂêëÊØèÊ¨°ÈÄöËøá`getIntBuffer`ÂæóÂà∞ÁöÑÁºìÂÜ≤Âå∫ÁöÑÂ§¥ÈÉ®
        double *dbp = columnVecs[2]-&gt;getDoubleBuffer(start, len, array_db_buf); //dbpÊåáÂêëÊØèÊ¨°ÈÄöËøá`getDoubleBuffer`ÂæóÂà∞ÁöÑÁºìÂÜ≤Âå∫ÁöÑÂ§¥ÈÉ®
        for (int i = 0; i &lt; len; ++i) {
            columnVecs[0]-&gt;setString(i+start, &quot;name_&quot;+std::to_string(++no)); //ÂØπstringÁ±ªÂûãÁöÑnameÂàóÁõ¥Êé•ËøõË°åËµãÂÄºÔºå‰∏çÈááÁî®getbufferÁöÑÊñπÂºè
            dtp[i] = 17898+i; 
            dbp[i] = (rand()%100)/3.0;
        }
        columnVecs[1]-&gt;setInt(start, len, dtp); //ÂÜôÂÆåÂêé‰ΩøÁî®`setInt`Â∞ÜÁºìÂÜ≤Âå∫ÂÜôÂõûÊï∞ÁªÑ
        columnVecs[2]-&gt;setDouble(start, len, dbp); //ÂÜôÂÆåÂêé‰ΩøÁî®`setDouble`Â∞ÜÁºìÂÜ≤Âå∫ÂÜôÂõûÊï∞ÁªÑ
        start += len;
    }
    return table;
}
```

