    func gestureRecognizer(_ gestureRecognizer: UIGestureRecognizer, shouldReceive touch: UITouch) -&gt; Bool {
    
       if touch.view == self.view.viewWithTag(viewTags.MySlider.rawValue) {
    		return false
       }
