    static int[] PowerOf10 = new[] { 1, 10, 100, 1000, 10000 };
    
    public int f3(int n) {
        var sign = Math.Sign(n == 0 ? 1 : n);
    
        n = Math.Abs(n);
        var digits = 1+(int)Math.Floor(Math.Log10(n == 0 ? 1 : n));
    
        var max = int.MinValue;    
        for (int j1 = 0; j1 &lt;= digits; ++j1) {
            var p10 = PowerOf10[j1];
            var right = n % p10;
            var newleft = 10*(n / p10)*p10;
            var val5 = 5*p10;
            var ans = sign*(newleft+val5+right);
            if (ans &gt; max)
                max = ans;
        }
        return max;
    }

