    var results = db.Carih.Where(c=&gt;c.FirmaKodu=someCode &amp;&amp; c.islemtipi=someItem &amp;&amp;....)
                    .GroupBy(c=&gt;c.islemtipi)
                    .Select(grp=&gt;new { islemtipi=grp.Key,
                                       AlcT=grp.Sum(c=&gt;c.AlcT),
                                       ...
                                     });

