			&lt;/tns:database&gt;&#39;;

	$dom=new DOMDocument;
	$dom-&gt;loadXML( $xml );
	
	$xp=new DOMXPath( $dom );
	$col=$xp-&gt;query(&#39;//ItemSpec[@id=&quot;57000&quot;]&#39;);
	
	if( $col-&gt;length &gt; 0 ){
		$attr=$dom-&gt;createAttribute(&#39;rewardid&#39;);
		$attr-&gt;nodeValue=&#39;banana&#39;;
		
		$node=$col-&gt;item(0);
