#include &lt;algorithm&gt;

using namespace std;

class Donor{
	public:
		Donor() noexcept: name(), contribution(0){}
    
		friend istream&amp; operator&gt;&gt;(istream&amp; stream, Donor&amp; donor){
			switch(stream.peek()){
				case  EOF: return stream;
