		friend ostream&amp; operator&lt;&lt;(ostream&amp; stream, const Donor&amp; donor){
			stream &lt;&lt; donor.name &lt;&lt; &#39; &#39; &lt;&lt; donor.contribution;

			return stream;
		}

		const string&amp; get_name() const noexcept{
			return name;
		}
		const double&amp; get_contribution() const noexcept{
			return contribution;
		}

	private:
