	bool grandpatrons_exist = any_of(begin(donors), end(donors), [](const Donor&amp; donor){ return donor.get_contribution() &gt;= 10000; });

	cout &lt;&lt; &quot;Here&#39;s the list of Grand Patrons:\n&quot;;
	if(grandpatrons_exist){
