    	std::string name;
    	std::string pincode;
    	int amount;
    };
    
    int main()
    {
    	std::vector&lt;users&gt; obj;
    	std::ifstream infile(&quot;users.txt&quot;);
    	if (!infile)
    	{
    		std::cout &lt;&lt; &quot;users.txt not found\n&quot;;
    		return 1;
    	}
    	for (int i = 0; i &lt; 10; i++)
