&lt;!-- begin snippet: js hide: false console: true babel: false --&gt;

&lt;!-- language: lang-css --&gt;

    .parent {
      border: 1px solid red;
      width: 50%;
      /* these rows will set up the grid */
      display: grid;
      /* 3 columns one third width of the container */
      grid-template-columns: repeat(3, 1fr);
    }

    .parent button {
      /* This will tell the buttons to align 
         themselves to the left of the grid cell 
         If you wish the buttons to fill the 
         whole width of the cell, you could remove this */
      justify-self: start;
    }

    /* First button in first cell */
    .parent button:first-child {
      grid-column: 1;
    }

    /* and so on... */
    .parent button:nth-child(2) {
      grid-column: 2;
    }

    .parent button:nth-child(3) {
      grid-column: 3;
    }

