&lt;!-- begin snippet: js hide: false console: true babel: false --&gt;

&lt;!-- language: lang-js --&gt;

    class ToggleButton extends React.Component {
      constructor(...props) {
        super(...props)
        this.state = {
          textIndex: 0,
          texts: [
            &#39;Hello Zeeshan 1&#39;,
            &#39;Hello Zeeshan 2&#39;,
            &#39;Hello Zeeshan 3&#39;
          ]
        };
      }
      
      rotateText(){
