        int
        &gt;, bool&gt; = false
    &gt;
    void info(IntType s) {

to

    template&lt;typename IntType,
        std::enable_if_t&lt;std::is_same_v&lt;    
        typename std::remove_reference_t&lt;typename std::remove_cv&lt;IntType&gt;::type&gt;  , 
