                LF &amp; HT &amp;
                &quot;sr_Data.r1_A = &quot; &amp; std_logic&#39;image(sr_Data.r1_A) &amp;
                LF &amp; HT &amp;
                &quot;sr_Data.r1_B = &quot; &amp; std_logic&#39;image(sr_Data.r1_B) &amp;
                LF &amp; HT &amp;
                &quot;sr_Data.ri_A = &quot; &amp; integer&#39;image(sr_Data.ri_A)   &amp;
                LF &amp; HT &amp;
                &quot;sr_Data.ri_B = &quot; &amp; integer&#39;image(sr_Data.ri_B);
