// sorts the items from L[i] through L[j]
void threewaysort(int[] L, int i, int j) {
    if (L[i] &gt; L[j]) swap (i, j);
    if (j - i + 1 &gt; 2) {
        t = (j - i + 1) / 3;
        threewaysort(L, i, j - t);
        threewaysort(L, i + t, j);
        threewaysort(L, i, j - t);
    }
}
