&lt;!-- language: lang-js --&gt;

    $(&#39;.form&#39;).submit(function(e) {
      e.preventDefault();
      $(&#39;#feedback&#39;).show();

      setTimeout(function() {
        $(&#39;.form&#39;)[0].submit();
      }, 3000);
    });

