    List&lt;Data&gt; dataFromJson(String str) =&gt; List&lt;Data&gt;.from(json.decode(str).map((x) =&gt; Data.fromMap(x)));
    
    String dataToJson(List&lt;Data&gt; data) =&gt; json.encode(List&lt;dynamic&gt;.from(data.map((x) =&gt; x.toMap())));
    
