    $dates = CarbonPeriod::create(&#39;2019-12-01&#39;, &#39;2019-12-12&#39;);
    $start_date = new Carbon(&quot;2019-12-01 00:00:00&quot;);

    $dates-&gt;forEach(function($date){
        $data = factory(Data::class)-&gt;create([
            &#39;date_playable&#39; =&gt; $date-&gt;toDateTimeString()
        ]);
