        std::cout &lt;&lt; (*it)[2] &lt;&lt; &#39;\n&#39;; // contains first group &quot;T&quot;
        std::cout &lt;&lt; (*it)[3] &lt;&lt; &#39;\n&#39;; // contains second group &quot;e&quot;
    }
}

