struct Node {
    char c;
    Node* next;
	Node* prev;
	Node(char cc):c(cc),next(nullptr),prev(nullptr) { };
};
using namespace std;

int main()
{
	char c;
	Node* start = nullptr;
	Node* end = nullptr;

	int i = 0;
	cout &lt;&lt; &quot;Original Linked List:&quot; &lt;&lt; endl;
	while (++i &lt;= 10)
	{
		c = _getch();
		Node* p = new Node(c);
		if (start == nullptr)
		{
			start = p;
			end = start;
			p-&gt;next = end;
			p-&gt;prev = start;
		}
		else
		{
			p-&gt;prev = end;
			end = p;
			p-&gt;next = end;
		}
		cout &lt;&lt; c;

	}
	cout &lt;&lt; endl;
	cout &lt;&lt; &quot;Reversed Linked List:&quot; &lt;&lt; endl;
	Node* curr = end;
	while (start != curr) {
		
		cout &lt;&lt; curr-&gt;c;
		start = curr-&gt;next;
