    const Title = (props) =&gt; {
            const { text } = props;
            return React.createElement(&#39;h1&#39;, null, text);
        }
    const App = (props) =&gt; {
        return React.createElement(&#39;div&#39;, null,
            React.createElement(Title, { text: &#39;Title one!&#39; } ),
            React.createElement(Title, { text: &#39;Title two!!&#39; } ),
            React.createElement(Title, { text: &#39;Title three!!!&#39; } )
